#include "compress.h"
#include "Dlog.h"

#include "uzlib.h"

#define COMPRESSION_LEVEL 1
#define CHUNK (1024)

char ht[16384];

static int xxx = 0;

int compress_memory(unsigned char *in, int in_length, unsigned char *out, int out_length)
{
	int ret;

    struct uzlib_comp comp = {0};
    comp.dict_size = 32768;
    comp.hash_bits = 12;
    int hash_size = sizeof(uzlib_hash_entry_t) * (1 << comp.hash_bits);
    //comp.hash_table = malloc(hash_size);
    comp.hash_table = (uzlib_hash_entry_t*)ht;
    if(comp.hash_table != NULL)
    {
    	memset(comp.hash_table, 0, hash_size);

    	zlib_start_block(&comp.out);
    	uzlib_compress(&comp, in, in_length);
    	zlib_finish_block(&comp.out);

    	Dlog_printf("\nSize of compressed data is: %u\n", comp.out.outlen);

    	memcpy(out, comp.out.outbuf, comp.out.outlen);
    	ret = comp.out.outlen;
        free(comp.out.outbuf);
/*
    	for(int i = 0; i < 10; i++)
    	{
    		Dlog_printf("0x%.02x, ", comp.out.outbuf[i]);
    	}
    	Dlog_printf("\n");
*/
    }
    else
    {
    	return -1;
    }

	return ret;
}

int decompress_memory(unsigned char *in, int in_length, unsigned char *out, int out_length)
{
    uzlib_init();
    struct uzlib_uncomp d;
    uzlib_uncompress_init(&d, ht, 16384);


    d.source = in;
    d.source_limit = in + out_length;//19
    d.source_read_cb = NULL;
/*
    int res = uzlib_gzip_parse_header(&d);
    if (res != TINF_OK) {
    	Dlog_printf("Error parsing header: %d\n", res);
    }
*/
    d.dest_start = d.dest = out;
    int res;
    int dlen = 1024;
    int OUT_CHUNK_SIZE = 1024;
    while (dlen) {
        unsigned int chunk_len = dlen < OUT_CHUNK_SIZE ? dlen : OUT_CHUNK_SIZE;
        d.dest_limit = d.dest + chunk_len;
        //res = uzlib_uncompress_chksum(&d);
        res = uzlib_uncompress(&d);
        dlen -= chunk_len;
        if (res != TINF_OK) {
        	Dlog_printf("\nTINF_OK\n");
            break;
        }
    }

    if (res != TINF_DONE) {
    	Dlog_printf("Error during decompression: %d\n", res);
    }

    Dlog_printf("\ny: ");
    for(int i = 0; i < 10; i++)
    {
    	Dlog_printf("%c ", out[i]);
    }

//    Dlog_printf("\n#%d", ++xxx);

	return 0;
}

